"""
bhch14exrc05.py: Write a class called Wordplay. It should have a field that holds a list of words. The user of the class should pass the list of words they want to use to the class. There should be the following methods:

words_with_length(length) — returns a list of all the words of length length
starts_with(s) — returns a list of all the words that start with s
ends_with(s) — returns a list of all the words that end with s
palindromes() — returns a list of all the palindromes in the list
only(L) — returns a list of the words that contain only those letters in L
avoids(L) — returns a list of the words that contain none of the letters in L
"""
print('*'*80)

class Wordplay:
	def __init__(self, words):
		self.words = words[:]
	
	def words_with_length(self, length):
		l = [i for i in self.words if len(i) == length]
		return l

	def starts_with(self, s):
		k = len(s)
		l = [i for i in self.words if s == i[:k]]
		return l

	def ends_with(self, s):
		k = len(s)
		l = [i for i in self.words if s == i[-k:] ]
		return l
	
	def palindromes(self):
		l = [i for i in self.words if len(i) > 2 and i == i[::-1]]
		return l

	def only(self, L):
		l = []
		for i in self.words:
			for j in i:
				if j not in L:
					break
			else:
				l.append(i)
		return l

	def avoids(self, L):
		l = []
		for i in self.words:
			for j in L:
				if j in i:
					break
			else:
				l.append(i)
		return l

all_words = [line.strip().lower() for line in open('wordlist.txt')]
words = Wordplay(all_words)

print('All words with length {:}:'.format(6))
print(words.words_with_length(6))

print('A list of words that start with \'{:}\':'.format('rt'))
print(words.starts_with('rt'))

print('A list of words that end with \'{:}\':'.format('sm'))
print(words.ends_with('sm'))

print('All available palindromes:')
print(words.palindromes())

print('The words that contain only the letters from \'{:}\''.format('ste'))
print(words.only('ste'))

print('The words that contain none of the letters from \'{:}\''.format('ste'))
print(words.avoids('ste'))

print('*'*80)
